import React, { useState } from 'react';

function ListaDeStrings() {
    const strings = ["Bienvenido al quiz de componentes de react", "Cómo se define un nuevo componente en React?", "Cómo se define un nuevo estado?", "F o V ... El inicializador del estdo puede ser una función.", "Qué es el virtual DOM? "];
    const [currentIndex, setCurrentIndex] = useState(0);

    const handleAvanzar = () => {
        if (currentIndex < strings.length - 1) {
            setCurrentIndex(currentIndex + 1);
        }
    };

    const handleRetroceder = () => {
        if (currentIndex > 0) {
            setCurrentIndex(currentIndex - 1);
        }
    };

    const handleReiniciar = () => {
        if (currentIndex > 0) {
            setCurrentIndex(0);
        }
    };

    return (
        <div className="text-center mt-3">

            <div>
                <button onClick={handleReiniciar} disabled={currentIndex === 0}>
                    Reiniciar
                </button>
                <button onClick={handleRetroceder} disabled={currentIndex === 0}>
                    Atrás
                </button>
                <button onClick={handleAvanzar} disabled={currentIndex === strings.length - 1}>
                    Adelante
                </button>

            </div>
            <div>{strings[currentIndex]}</div>
        </div>
    );
}

export default ListaDeStrings;
